import React, { Component } from 'react';
import './App.css';

class App extends Component {
	render() {
		return (
			<div className="App">
			<SizeInput />
			<Grid size="10"/>
			</div>
			);
	}
}

class SizeInput extends Component {
	render() {
		return (
			<p>hi</p>
			);
	}
}

class Grid extends Component {
	render() {
		return (
			<center>
			<table>
			{this.createTable()}
			</table>
			</center>
			);
	}

	createTable = () => {
		let table = [];

		for (let i = 0; i < this.props.size; i++) {
			let children = [];

			for (let j = 0; j < this.props.size; j++) {

				children.push(<td className="sleek"><Space iValue="3" jValue="4" size="10"/></td>);
			}

			table.push(<tr className="sleek">{children}</tr>)
		}

		return table;
	};
}

class Space extends Component {
	constructor(props) {
		super(props);
		this.state = {
			isOn: true,
			bgColor: "rgb(255, 255, 0)"
		};
		/*this.handleClick = this.handleClick.bind(this);*/
	}

	/*handleClick() {
