			this.setState(state => ({
				isOn: false,
				bgColor: {bg}
			}));
		} else {
			this.setState(state => ({
				isOn: true,
				bgColor: "rgb(0, 255, 255)"
			}));
		}
	}*/

	render() {
		return (
			<button className="sleek" backgroundColor={this.state.bgColor} width={"" + 80/this.props.size + "vw"} height={"" + 80/this.props.size + "vh"} /*onClick={this.handleClick}*/>e</button>
			);
	}
}

export default App;
