import React, { Component } from 'react';
import './App.css';

class App extends Component {
	render() {
		let size = 10;
		return (
			<div className="App">
			<SizeInput size={size}/>
			<Grid size={size}/>
			</div>
			);
	}
}

class SizeInput extends React.Component {
	constructor(props) {
		super(props);
		this.state = {value: ''};

		this.handleChange = this.handleChange.bind(this);
	}

	handleChange(event) {
		this.setState({value: event.target.value.toUpperCase()});
	}

	render() {
		return (
			<input type="text" value={this.state.value} onChange={this.handleChange} />
			);
	}
}

class Grid extends Component {
	render() {
		return (
			<center>
			<table>
			<tbody>
			{this.createTable()}
			</tbody>
			</table>
			</center>
			);
	}

	createTable = () => {
		let table = [];

		for (let i = 0; i < this.props.size; i++) {
			let children = [];

			for (let j = 0; j < this.props.size; j++) {
				console.log(this.props.size);
				children.push(<td><Space iValue={i} jValue={j} size={this.props.size}/></td>);
			}

			table.push(<tr>{children}</tr>)
		}

		return table;
	};
}

class Space extends Component {
	constructor(props) {
		super(props);
		const bg = "rgb(" + Math.floor(this.props.iValue / this.props.size * 255) + ", " + Math.floor(this.props.jValue / this.props.size * 255) + ", 0)";
		this.state = {
			isOn: true,
			bgColor: bg
		};
		this.handleClick = this.handleClick.bind(this);
	}

	handleClick() {
		if (this.state.isOn) {
			const bg = "rgb(" + Math.floor(this.props.iValue / this.props.size * 255) + ", " + Math.floor(this.props.jValue / this.props.size * 255) + ", 0)";
			this.setState(state => ({
				isOn: false,
				bgColor: bg
			}));
		} else {
			const bg = "rgb(" + (255 - Math.floor(this.props.iValue / this.props.size * 255)) + ", " + (255 - Math.floor(this.props.jValue / this.props.size * 255)) + ", 255)";
			this.setState(state => ({
				isOn: true,
				bgColor: bg
			}));
		}
	}

	render() {
		const bStyle = {
			backgroundColor: this.state.bgColor,
			width: 80/this.props.size + "vw",
			height: 80/this.props.size + "vh"
		};
		return (
			<button className="sleek" style={bStyle} onMouseOver={this.handleClick}></button>
			);
	}
}

export default App;
