import React, { Component } from 'react';
import './App.css';

class App extends Component {
	constructor(props) {
		super(props);
		this.handleSizeChange = this.handleSizeChange.bind(this);
		this.state = {size: 10};
	}

	handleSizeChange(size) {
		this.setState({size});
	}

	render() {
		return (
			<div className="App">
			<SizeInput size={this.state.size} onSizeChange={this.handleSizeChange}/>
			<Grid size={this.state.size}/>
			</div>
			);
	}
}

class SizeInput extends React.Component {
	constructor(props) {
		super(props);
		this.state = {focused: false};

		this.handleChange = this.handleChange.bind(this);
		this.handleFocus = this.handleFocus.bind(this);
	}

	handleChange(e) {
		this.state = {focused: false};

		const val = e.target.value;
		if (!(isNaN(val))) {
			this.props.onSizeChange(Math.min(val, 15));
		}
	}

	handleFocus(e) {
		//e.preventDefault();
		this.state = {focused: true};
	}

	render() {
		return (
			<input type="text" value={this.state.focused ? "" : this.props.size} onChange={this.handleChange} onFocus={this.handleFocus} />
			);
	}
}

class Grid extends Component {
	render() {
		return (
			<center>
			<table>
			<tbody>
			{this.createTable()}
			</tbody>
			</table>
			</center>
			);
	}

	createTable = () => {
		let table = [];

		for (let i = 0; i < this.props.size; i++) {
			let children = [];

			for (let j = 0; j < this.props.size; j++) {
				console.log(this.props.size);
				children.push(<Space iValue={i} jValue={j} size={this.props.size}/>);
			}

			table.push(<tr>{children}</tr>)
		}

		return table;
	};
}

class Space extends Component {
	constructor(props) {
		super(props);
		const bg = "rgb(" + Math.floor(this.props.iValue / this.props.size * 255) + ", " + Math.floor(this.props.jValue / this.props.size * 255) + ", 0)";
		this.state = {
			isOn: true,
			bgColor: bg
		};
		this.handleClick = this.handleClick.bind(this);
	}

	handleClick() {
		if (this.state.isOn) {
			const bg = "rgb(" + Math.floor(this.props.iValue / this.props.size * 255) + ", " + Math.floor(this.props.jValue / this.props.size * 255) + ", 0)";
			this.setState(state => ({
				isOn: false,
				bgColor: bg
			}));
		} else {
			const bg = "rgb(" + (255 - Math.floor(this.props.iValue / this.props.size * 255)) + ", " + (255 - Math.floor(this.props.jValue / this.props.size * 255)) + ", 255)";
			this.setState(state => ({
				isOn: true,
				bgColor: bg
			}));
		}
	}

	render() {
		const bStyle = {
			backgroundColor: this.state.bgColor,
			width: 80/this.props.size + "vw",
			height: 80/this.props.size + "vh"
		};
		return (
			<td><button className="sleek" style={bStyle} onClick={this.handleClick}></button></td>
			);
	}
}

export default App;
