import React, { Component } from 'react';
import './App.css';

class App extends Component {
	render() {
		return (
			<div className="App">
			<br/>
			<br/>
			<Grid />
			</div>
			);
	}
}

const size = 20;
var listItems;

class Grid extends Component {
	render() {
		return (
			<center>
			<table>
				{this.createTable()}
			</table>
			</center>
			);
	}



	createTable = () => {
		let table = [];

		for (let i = 0; i < size; i++) {
			let children = [];

			for (let j = 0; j < size; j++) {
				const buttonStyle = {
					backgroundColor: "rgb(" + Math.floor(i / size * 255) + ", " + Math.floor(j / size * 255) + ", 0)",
					width: 1/size*80 + "vw",
					height: 1/size*80 + "vh",
					margin:0,
					padding:0
				};
				children.push(<td style={{margin:0, padding:0}}><button style={buttonStyle}></button></td>);
			}

			table.push(<tr>{children}</tr>)
		}

		return table;
	};
}

export default App;
