import React, { Component } from 'react';
import './App.css';

const size = 20;

class App extends Component {
	render() {
		return (
			<div className="App">
			<SizeInput />
			<Grid />
			</div>
			);
	}
}

class SizeInput extends Component {
	render() {
		return (
			<p>hi</p>
		);
	}
}

class Grid extends Component {
	render() {
		return (
			<center>
			<table>
				{this.createTable()}
			</table>
			</center>
			);
	}

	createTable = () => {
		let table = [];

		for (let i = 0; i < size; i++) {
			let children = [];
			const sleekStyle = {
				margin:0,
				padding:0
			};

			for (let j = 0; j < size; j++) {
				const buttonStyle = {
					backgroundColor: "rgb(" + Math.floor(i / size * 255) + ", " + Math.floor(j / size * 255) + ", 0)",
					width: 1/size*80 + "vw",
					height: 1/size*80 + "vh",
					margin:0,
					padding:0,
					border:0
				};
				children.push(<td style={sleekStyle}><button style={buttonStyle}></button></td>);
			}

			table.push(<tr style={sleekStyle}>{children}</tr>)
		}

		return table;
	};
}

export default App;
