import React, { Component } from 'react';
import './App.css';

const size = 20;

class App extends Component {
	render() {
		return (
			<div className="App">
			<SizeInput />
			<Grid />
			</div>
			);
	}
}

class SizeInput extends Component {
	render() {
		return (
			<p>hi</p>
			);
	}
}

class Grid extends Component {
	render() {
		return (
			<center>
			<table>
			{this.createTable()}
			</table>
			</center>
			);
	}

	createTable = () => {
		let table = [];

		for (let i = 0; i < size; i++) {
			let children = [];
			const sleekStyle = {
				margin:0,
				padding:0,
				border:0
			};

			for (let j = 0; j < size; j++) {

				children.push(<td style={sleekStyle}><Space iValue={i} jValue={j}/></td>);
			}

			table.push(<tr style={sleekStyle}>{children}</tr>)
		}

		return table;
	};
}

class Space extends Component {
	constructor(props) {
		super(props);
		this.state = {
			isOn: false,
			bgColor: "rgb(" + Math.floor(props.iValue / size * 255) + ", " + Math.floor(props.jValue / size * 255) + ", 0)"
		};
		this.handleClick = this.handleClick.bind(this);
	}

	handleClick() {
		if (this.state.isOn) {
			const bg = "rgb(" + Math.floor(this.props.iValue / size * 255) + ", " + Math.floor(this.props.jValue / size * 255) + ", 0)";
			this.setState(state => ({
				isOn: false,
				bgColor: {bg}
			}));
		} else {
			this.setState(state => ({
				isOn: true,
				bgColor: "rgb(255, 255, 255)"
			}));
		}
	}

	render() {
		return (
			<button className="space" backgroundColor={this.state.bgColor} width={1/size*80 + "vw"} height={1/size*80 + "vh"} onClick={this.handleClick} />
			);
	}
}

export default App;
